import Button from '@material-ui/core/Button';

class CSVComp extends Component {
  constructor(props) {
    super(props)
    this.fileInput = React.createRef()
  }
  componentDidMount() {
    this.convertDataToNumbers()
  }

  handleReadCSV = (data) => {
    console.log(data)
    this.props.setMyData(this.convertToObject(data))


  }

  handleOnError = (err, file, inputElem, reason) => {
    console.log(err)
  }

  handleImportOffer = () => {
    this.fileInput.current.click()
  }

  convertToObject = (anArray) => {
    var data = anArray,
    keys = data.data[0],
    result = data.data.slice(2).map(function (a) {
        var temp = {};
        keys.forEach(function (k, i) {
            temp[k] = a[i];
        })
        return temp;
    });

    return result

};
convertDataToNumbers = () => {
  const myData = [
    {
      amount: '$5.00',
      test: 'test'
    },
    {
      amount: '$10.00',
      test: 'test'
    }
  ]

  myData.forEach(data => console.log(myData))

}

  render() {
    return (
      <>
        <CSVReader
          onFileLoaded={this.handleReadCSV}
          inputRef={this.fileInput}
          style={{display: 'none'}}
          onError={this.handleOnError}
        />

        <Button variant="contained" color="primary" onClick={this.handleImportOffer}>
            Import
        </Button>

      </>
    )
  }
}

export default CSVComp
